with.converter:
if env.get("__features").is_empty() {
  []
} else {
  let result = [];
  let feature = env.get("__features")[0];
  for key in feature.keys() {
    result.push(#{
      name: key,
      count: feature[key],
    });
  }
  result
}

operations.value:
let total = 0;
for key in env.get("__value").keys() {
  let val = env.get("__value")[key];
  if val.type_of() == "i64" {
    total += val;
  }
}
total

